import React,{useState} from "react";
import {Link, withRouter} from "react-router-dom";
import Proptypes from "prop-types";
import {connect} from "react-redux";
import registerUser from "../../actions/authActions";


const Register = () => {

    const [state, setState] = useState({
    name:"",
    email: "",
    password: "",
    password2:"",
    errors: {}
    });
    const [newUser, setNewUserData] = useState([]);

    const errors = state.errors;




    const HandleChange = (event) =>{
        const {name, value} = event.target;

            setState(prevValue => {
                return {
                    ...prevValue, [name]: value
                }
            });

            console.log(state);

    };

    const propUpdate = (nextProps) => {

    const  [state, setState] = useState(false);
    const  [prevState, setPrevState] = useState(null);

    if(nextProps.errors !== prevState.errors){
      setState(errors= nextProps.errors);
    }
    }

    const HandleSubmit = (event) => {
            event.preventDefault();

            setNewUserData((prevState) => {
                return[
                    ...prevState, state
                ]
            });

            props.registerUser(newUser, props.history);

            console.log(newUser);

    };



    return (
        <div>
        <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect" style={{ paddingRight: "410px" }}>
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s0" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Register</b> below
              </h4>
              <p className="grey-text text-darken-1" style={{ paddingRight: "8px" }}>
                Already have an account? <Link to="/login">Log in</Link>
              </p>
            </div>
            <form noValidate >
              <div className="input-field col s12">
                <input
               onChange={HandleChange}
               name="name"
               type="text"
               error={errors.name}
               className={classnames("", {
                    invalid: errors.name
                  })}
                />
                <label htmlFor="name">Name</label>
                <span className="red-text">{errors.name}</span>

              </div>
              <div className="input-field col s12">
                <input
                onChange={HandleChange}
                name="email"
                type="email"
                error={errors.email}
                className={classnames("", {
                    invalid: errors.email
                  })}
                />
                <label htmlFor="email">Email</label>
                <span className="red-text">{errors.email}</span>

              </div>
              <div className="input-field col s12">
                <input
               onChange={HandleChange}
               name="password"
               type="password"
               error={errors.password}
               className={classnames("", {
                    invalid: errors.password
                  })}
                />
                <label htmlFor="password">Password</label>
                <span className="red-text">{errors.password}</span>

              </div>
              <div className="input-field col s12">
                <input
                   onChange={HandleChange}
                   name="password2"
                   type="password"
                  error={errors.password2}

                  className={classnames("", {
                    invalid: errors.password2
                  })}
                />
                <label htmlFor="password2">Confirm Password</label>
                <span className="red-text">{errors.password2}</span>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  type="submit"   
                  onClick={HandleSubmit}  
                >
                  Sign up
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
        </div>
    )
};

Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { registerUser }
)(withRouter(Register));
